import { useState } from 'react';
import { Route, Routes, Navigate, Link } from 'react-router-dom';
import AuthService from './services/auth.service';
import Layout from './components/layout/Layout/Layout';
import StartingPage from './pages/StartingPage/StartingPage';
import Login from './pages/Login/Login';
import Recipes from './pages/Recipes/Recipes/Recipes';
import RecipeDetail from './pages/RecipeDetail/RecipeDetail';
import Comments from './components/comments/comments/Comments';
import AddYourTwist from './pages/AddYourTwist/AddYourTwist';
import FellowBartenders from './pages/FellowBartenders/FellowBartenders/FellowBartenders';
import PageNotFound from './pages/PageNotFound';
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [currentUser, setCurrentUser] = useState(undefined);

  useEffect(() => {
    const user = AuthService.getCurrentUser();

    if (user) {setCurrentUser(user)};
  }, []);

  return (
    <Layout>
      <Routes>
        {!currentUser && <Route path='*' element={<Navigate replace to='/' />}/>}
        {!currentUser && <Route path='/' element={<StartingPage />}/>}
        {!currentUser && <Route path='/login' element={<Login />}/>}
        {currentUser && <Route path='/*' element={<Navigate replace to='/recipes' />}/>}
        {currentUser && <Route path='/recipes' element={<Recipes />}/>}
        {currentUser && (
          <Route path='/recipes/:recipeId' element={<RecipeDetail />}>
            <Route
              path=''
              element={
                <div className='centered'>
                  <Link className='btn--flat' to={`comments`}>
                    Load Comments
                  </Link>
                </div>
              }
            />
            <Route path={`comments`} element={<Comments />} />
          </Route>
        )}
        {currentUser && <Route path='/add-your-twist' element={<AddYourTwist />}/>}
        {currentUser && <Route path='/fellow-bartenders' element={<FellowBartenders />}/>}
        {currentUser && <Route path='/profile' element={<Profile />} />}
        {/* {currentUser && <Route path='/profile/edit-info' element={<EditInfo />} />} */}
        {currentUser && <Route path='*' element={<PageNotFound />} />}
      </Routes>
    </Layout>
  );
};

export default App;
