import { Route, Routes, Navigate, Link } from 'react-router-dom';
import Layout from './components/layout/Layout/Layout';
import Recipes from './pages/Recipes/Recipes';
import RecipeDetail from './pages/RecipeDetail/RecipeDetail';
import Comments from './components/comments/Comments';
import AddYourTwist from './pages/AddYourTwist/AddYourTwist';
import NotFound from './pages/Recipes/components/not-found/NotFound';

const App = () => {
  return (
    <Layout>
      <Routes>
        <Route path='/' element={<Navigate replace to='/recipes' />} />
        <Route path='/recipes' element={<Recipes />}/>
        <Route path='/recipes/:recipeId' element={<RecipeDetail />}>
          <Route
            path=''
            element={
              <div className='centered'>
                <Link className='btn--flat' to={`comments`}>
                  Load Comments
                </Link>
              </div>
            }
          />
          <Route path={`comments`} element={<Comments />} />
        </Route>
        <Route path='/add-your-twist' element={<AddYourTwist />}/>
        <Route path='*' element={<NotFound />} />
      </Routes>
    </Layout>
  )
};

export default App;
