import { Outlet, useParams } from 'react-router-dom';
import { Fragment } from 'react';
import MarkedRecipe from '../Recipes/components/marked/MarkedRecipe';

const DUMMY_DATA = [
    { id: '1', drinkName: 'GIN FIZZ', bartender: 'AMY'},
    { id: '2', drinkName: 'COSMO', bartender: 'BEA'},
    { id: '3', drinkName: 'LONG ISLAND', bartender: 'CATHY'},
    { id: '4', drinkName: 'TEQUILA SUNRISE', bartender: 'DEVON'},
    { id: '5', drinkName: 'HIGHBALL', bartender: 'ELLE'},
];

const RecipeDetail = () => {
    const params = useParams();

    const recipe = DUMMY_DATA.find(recipe => recipe.id === params.recipeId);

    if (!recipe) {
        return <p>No RECIPE FOUND</p>
    };

    return(
        <Fragment>
            <MarkedRecipe drinkName={recipe.drinkName} bartender={recipe.bartender} />
            <Outlet />
        </Fragment>
    )
};

export default RecipeDetail;
