import { useRef } from 'react';

import Card from '../../../../components/UI/Card/Card';
import LoadingSpinner from '../../../../components/UI/LoadingSpinner/LoadingSpinner';
import classes from './RecipeForm.module.css';

const RecipeForm = (props) => {
  const [isEntering, setIsEntering] = useState(false); 
  const drinkNameInputRef = useRef();
  const bartenderInputRef = useRef();

  const submitFormHandler = (event) => {
    event.preventDefault();

    const enteredDrinkName = drinkNameInputRef.current.value;
    const enteredBartender = bartenderInputRef.current.value;

    // optional: Could validate here

    props.onAddRecipe({ drinkName: enteredDrinkName, bartender: enteredBartender });
  }

  const finishEnteringHandler = () => {
    setIsEntering(false);
  };

  const formFocusedHandler = () => {
    setIsEntering(true);
  };

  return (
    <Card>
      <form className={classes.form} onSubmit={submitFormHandler} onFocus={formFocusedHandler}>
        {props.isLoading && (
          <div className={classes.loading}>
            <LoadingSpinner />
          </div>
        )}
        <div className={classes.control}>
          <label htmlFor='drinkName'>Drink Name</label>
          <input type='text' id='drinkName' ref={drinkNameInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor='bartender'>Bartender</label>
          <input type='text' id='bartender' ref={bartenderInputRef} />
        </div>

        <div className={classes.actions}>
          <button className='btn'>Add Recipe</button>
        </div>
      </form>
    </Card>
  );
};

export default RecipeForm;
