import { useState } from 'react';

import classes from './NewCommentForm.module.css';

const NewCommentForm = (props) => {
  const [rating, setRating] = useState(0);
  const [ratingHover, setRatingHover] = useState(0);
  const [commentInput, setCommentInput] = useState('');

  const updateCommentHandler = (event) => {
    event.preventDefault();
    setCommentInput(event.target.value);
  };

  const submitFormHandler = (event) => {
    event.preventDefault();

    // optional: Could validate here
    if (commentInput === '') {
      alert('Please share your opinion!');
      return;
    };

    // send comment to server
  };

  return (
    <form className={classes.form} onSubmit={submitFormHandler}>
      <div className={classes.control} onSubmit={submitFormHandler}>
        <label htmlFor='comment'>Your thoughts?</label>
        <div className={classes.star}>
          <h5>Rating</h5>
          {[...Array(5)].map((star, index) => {
            index += 1;
            return (
              <button
                type="button"
                key={index}
                className={index <= (ratingHover || rating) ? "on" : "off"}
                onClick={() => setRating(index)}
                onMouseEnter={() => setRatingHover(index)}
                onMouseLeave={() => setRatingHover(rating)}
              >
                <span>&#9733;</span>
              </button>
            );
          })}
        </div>
        <textarea id='comment' rows='3' onChange={updateCommentHandler}></textarea>
      </div>
      <div className={classes.actions}>
        <button className='btn'>Publish</button>
      </div>
    </form>
  );
};

export default NewCommentForm;
