import { useState } from 'react';
import RecipeList from './components/list/RecipeList';

const DUMMY_DATA = [
    { id: '1', drinkName: 'GIN FIZZ', bartender: 'AMY'},
    { id: '2', drinkName: 'COSMO', bartender: 'BEA'},
    { id: '3', drinkName: 'LONG ISLAND', bartender: 'CATHY'},
    { id: '4', drinkName: 'TEQUILA SUNRISE', bartender: 'DEVON'},
    { id: '5', drinkName: 'HIGHBALL', bartender: 'ELLE'},
];

const Recipes = () => {
    const [searchInput, setsearchInput] = useState('');

    const changeHandler = (event) => {
        event.preventDefault();
        setsearchInput(event.target.value);
    };

    if (searchInput.length > 0) {
        DUMMY_DATA.filter((recipe) => {
            return (
                recipe.drinkName.toLowerCase().includes(searchInput.toLowerCase()) ||
                recipe.bartender.toLowerCase().includes(searchInput.toLowerCase())
            );
        });
    };

    return (
        <div>
            <input
                type='search'
                placeholder='Search by Drink Name or Bartender'
                value={searchInput}
                onChange={changeHandler}
            />
            <RecipeList recipes={DUMMY_DATA} />
        </div>
    )
};

export default Recipes;
